insert into Steps(StepName) values ('Айву вымыть, удалить сердцевину и нарезать небольшими кусочками.')
insert into Steps(StepName) values ('Айву разрезать, удалить сердцевину, нарезать крупными дольками. Половинку лимона разрезать на 4 части. Сложить в кастрюлю, влить 500 мл воды, довести до кипения и варить 20-30 минут до мягкости айвы.')
insert into Steps(StepName) values ('Аромата и вкуса водки не присутствует.')
insert into Steps(StepName) values ('В конце добавить измельченный чеснок.')
insert into Steps(StepName) values ('В отдельной посуде смешать муку и молоко до образования густой массы.')
insert into Steps(StepName) values ('В пакет положите сахар, соль и перец горошком.')
insert into Steps(StepName) values ('В процессе варки айва размягчится, образуется вкуснейший густой сироп.')
insert into Steps(StepName) values ('В процессе тушения посолить и посыпать смесью перцев.')
insert into Steps(StepName) values ('В сковороде разогреть сливочное масло, выложить айву и прогреть, постоянно перемешивая, в течение 5 минут.')
insert into Steps(StepName) values ('В течение 3-х дней по несколько раз в день мясо переворачивайте и встряхивайте пакет, чтобы оно равномерно промариновалось.')
insert into Steps(StepName) values ('В форму для выпечки помещаем заранее вырезанную пергаментную бумагу, предназначенную для выпечки.')
insert into Steps(StepName) values ('Варить айву в течение 1-1,5 часов, постоянно помешивая.')
insert into Steps(StepName) values ('Включаем режим «Тушение», время 1,5 часа. 6. После того, как прозвучит сигнал, добавляем мелко нарезанную зелень.')
insert into Steps(StepName) values ('Включить режим «Жарка», время 30 минут.')
insert into Steps(StepName) values ('Влить лимонный сок, посыпать сахаром, перемешать и готовить на среднем огне, помешивая, минут 10, чтобы сахар карамелизировался, а айва стала мягче.')
insert into Steps(StepName) values ('Влить теплое молоко, все перемешать.')
insert into Steps(StepName) values ('Возьмите пакет для замораживания продуктов, вставьте его в ёмкость, в которой будете мариновать мясо.')
insert into Steps(StepName) values ('Все вместе обжариваем.')
insert into Steps(StepName) values ('Все ингредиенты тщательно перемешиваем в специальной миске.')
insert into Steps(StepName) values ('Всыпать сахар, перемешать и поставить на средний огонь.')
insert into Steps(StepName) values ('Вымыть лимоны и айву')
insert into Steps(StepName) values ('Говядину порежьте на тонкие пластины и отбейте молоточком с обеих сторон.')
insert into Steps(StepName) values ('Горячий мармелад выложить в форму, лучше всего стеклянную или силиконовую, смазывать маслом или застилать бумагой не нужно. Оставить при комнатной температуре на 10-12 часов.')
insert into Steps(StepName) values ('Готовое тесто размещаем на вырезанный круг бумаги и раскатываем до концов окружности. Готовый круг из теста и бумаги размещаем в форме для выпечки и формируем бортики.')
insert into Steps(StepName) values ('Готовый пирог достать из духовки и остудить.')
insert into Steps(StepName) values ('Добавим чеснок в морковку.')
insert into Steps(StepName) values ('Для приготовления начинки нам понадобится: 5 яиц, 180 г. сахара, лимонный сок и щепотка соли, 2 ст. ложки муки и 3 ст. ложки молока, 900 г. творога, цедра 1 лимона.')
insert into Steps(StepName) values ('Для приготовления посыпки замороженное сливочное масло натереть на терке.')
insert into Steps(StepName) values ('Для теста основы нам понадобится: 200 г. муки (лучше просеянной), 65 г. сахара, 65 г. сливочного масла.')
insert into Steps(StepName) values ('Добавить муку, сахар и растолченные крупно орехи.')
insert into Steps(StepName) values ('Добавить порезанный мелко лук, обжарить вместе с мясом 3-4 минуты.')
insert into Steps(StepName) values ('Добавляем к мясу.')
insert into Steps(StepName) values ('Добавляем сахар, 0.5 ч.л. красного перца в морковь.')
insert into Steps(StepName) values ('Добавьте муку и еще раз хорошенько перемешайте.')
insert into Steps(StepName) values ('Добавьте немного сливочного масла на каждый блинчик.')
insert into Steps(StepName) values ('Добавьте сметану, перемешайте, прогрейте пару минут.')
insert into Steps(StepName) values ('Добавьте соду, сахар и соль')
insert into Steps(StepName) values ('Добавьте специи и посолите.')
insert into Steps(StepName) values ('Добавьте яичную смесь к мясу и хорошенько перемешайте.')
insert into Steps(StepName) values ('Довести варенье до кипения, помешивая айву, проварить 2 минуты и выключить.')
insert into Steps(StepName) values ('Довести до кипения, все время мешая.')
insert into Steps(StepName) values ('Долить воды.')
insert into Steps(StepName) values ('Если слишком быстро испарится вся жидкость, можно добавить немного воды или бульона.')
insert into Steps(StepName) values ('Если таковой нет, можно постараться нарезать.')
insert into Steps(StepName) values ('Жареный лаваш с начинкой подать в горячем виде.')
insert into Steps(StepName) values ('Желтки отделить от белков.')
insert into Steps(StepName) values ('Желтки смешать с сахаром, а белки взбить с лимонным соком и щепоткой соли до образования пены.')
insert into Steps(StepName) values ('Заверните блинчик.')
insert into Steps(StepName) values ('Закрываем крышкой и позволяем блюду потомиться еще 15 мин, напитаться ароматами.')
insert into Steps(StepName) values ('Залейте масло. Возьмите половник и зачерпните тесто несколько раз.')
insert into Steps(StepName) values ('Заливаем горячим маслом морковку.')
insert into Steps(StepName) values ('Заливаем смесью мясо и овощи.')
insert into Steps(StepName) values ('Заправьте мясной начинкой.')
insert into Steps(StepName) values ('Засыпать сахаром.')
insert into Steps(StepName) values ('Затем воду слить, лимон убрать. Айву очистить от кожуры (она легко снимется) и нарезать небольшими кубиками.')
insert into Steps(StepName) values ('Затем выложите мясо, перемешайте и обжаривайте несколько минут на среднем огне, а затем убавьте огонь до минимума и готовьте 10-15 минут.')
insert into Steps(StepName) values ('Затем вынуть мармелад из формы, нарезать на квадратики (или другой формы) обвалять в сахаре.')
insert into Steps(StepName) values ('Затем добавьте томатную пасту, соль, перец. Перемешайте, накройте крышкой и готовьте на среднем огне в течение 30 минут.')
insert into Steps(StepName) values ('Затем на лаваш с сыром выложить сосиску.')
insert into Steps(StepName) values ('Затем порежьте эти пластины на тонкие полосочки (соломку).')
insert into Steps(StepName) values ('Затем снять с огня, протереть через сито или пюрировать блендером в однородную массу. Снова поставить на плиту и варить 15-20 минут на медленном огне, постоянно помешивая.')
insert into Steps(StepName) values ('Затем тесто обмять и оставить в теплом месте еще на 30 минут.Айву вымыть, удалить сердцевину и нарезать небольшими кусочками.')
insert into Steps(StepName) values ('И так продолжать варить 4 суток.')
insert into Steps(StepName) values ('Ингредиенты для морковки по-корейски.')
insert into Steps(StepName) values ('К взбитым белкам добавить мучную массу, ванильный порошок по вкусу и творог - всё тщательно смешать.')
insert into Steps(StepName) values ('К полученной массе добавляем 20 мл. воды. Необходимо вымесить твёрдое тесто. Если тесто липкое, советую его положить на некоторое время в холодильник.')
insert into Steps(StepName) values ('К полученной однородной массе добавить цедру одного лимона и хорошо перемешать.')
insert into Steps(StepName) values ('Классический бефстроганов готов!')
insert into Steps(StepName) values ('Когда тесто подойдет, посыпать поверх начинки крошкой и поставить пирог с айвой в разогретую до 200 градусов духовку на 25-30 минут. Теста получается немного больше, чем требуется для формы диаметром 25 см. Из остатков теста можно приготовить пирожки или булочки.')
insert into Steps(StepName) values ('Кожуру оставить.')
insert into Steps(StepName) values ('Края лаваша подогнуть и свернуть рулетом. Таким образом подготовить все рулетики.')
insert into Steps(StepName) values ('Куриное филе порежьте на небольшие по размеру пластины толщиной 5-6 мм. и обжарьте в небольшом количестве растительного масла до золотистой корочки. Идеально, если вы будете жарить их на гриле.')
insert into Steps(StepName) values ('Куриное филе порезать небольшими кусочками, посолить, посыпать специями, обжарить на растительном масле в течение 5-7 минут на среднем огне.')
insert into Steps(StepName) values ('Лаваш порезать на прямоугольники, размером приблизительно 15х20 см.')
insert into Steps(StepName) values ('Лаваш разрезать на прямоугольники длиной около 20 сантиментов и шириной немного больше, чем длина сосиски')
insert into Steps(StepName) values ('Лук порежьте тонкими полукольцами или четвертькольцами, если лук крупный.')
insert into Steps(StepName) values ('Лук чистим, перец очищаем от зерен.')
insert into Steps(StepName) values ('Мармелад из айвы готов.')
insert into Steps(StepName) values ('Можно и кубиком мелким порезать.')
insert into Steps(StepName) values ('Можно переложить варенье в баночку, вазочку.')
insert into Steps(StepName) values ('Морковь чистим, натираем на крупную терку.')
insert into Steps(StepName) values ('Мясо без труда нарезается тоненькими кусочками.')
insert into Steps(StepName) values ('Мясо готово.')
insert into Steps(StepName) values ('Мясо промыть под проточной водой.')
insert into Steps(StepName) values ('На край лаваша выложить 1-2 столовые ложки начинки. Сверху - майонез (можно еще добавить по желанию любую зелень, сыр).')
insert into Steps(StepName) values ('На сковороде разогрейте растительное масло, выложите лук и обжарьте на среднем огне до золотистого цвета.')
