insert into Steps(StepName) values ('Накройте крышкой и готовьте в течении 20 минут.')
insert into Steps(StepName) values ('Накройте крышкой и тушите еще 5 минут.')
insert into Steps(StepName) values ('Налейте подсолнечное масло и водку.')
insert into Steps(StepName) values ('Нарежьте мясо тоненькими кусочками.')
insert into Steps(StepName) values ('Нарезаем лук крупно. Обжариваем лучок в растительном масле до золотистого цвета.')
insert into Steps(StepName) values ('Нарезать кубиками.')
insert into Steps(StepName) values ('Нарезать на четвертинки, очистить от семечек.')
insert into Steps(StepName) values ('Натираем морковь длинной соломкой с помощью специальной терки.')
