insert into Steps(StepName) values ('Начинку размещаем внутри выпеченной основы.')
insert into Steps(StepName) values ('Обжариваем вместе с мясом и овощами.')
insert into Steps(StepName) values ('Обжарить лаваш на среднем огне со всех сторон до золотистой хрустящей корочки (примерно, по 2 минуты с каждой стороны).')
insert into Steps(StepName) values ('Обжарить лаваш с начинкой на сковороде, в достаточном количестве растительного масла, с двух сторон до румяного цвета (около 3-4 минут с каждой стороны).')
insert into Steps(StepName) values ('Обмакните бумажными полотенцами.')
insert into Steps(StepName) values ('Овощи нарезаем кубиками.')
insert into Steps(StepName) values ('Оставьте в сторону на 15 минут.')
insert into Steps(StepName) values ('Отправьте в сковороду муку и хорошенько перемешайте, чтобы она равномерно распределилась.')
insert into Steps(StepName) values ('Отставить в сторонку.')
insert into Steps(StepName) values ('Пакет хорошо завяжите.')
insert into Steps(StepName) values ('Перед подачей на стол дайте морковке постоять в холодильнике примерно 2-3 часа.')
insert into Steps(StepName) values ('Перемешать руками в крошку, убрать в холодильник.')
insert into Steps(StepName) values ('Перемешиваем.')
insert into Steps(StepName) values ('Плавленый сыр натереть на терке и разровнять по поверхности лаваша.')
insert into Steps(StepName) values ('По истечении 20 минут, взбейте 1 яйцо с небольшим количеством воды.')
insert into Steps(StepName) values ('Подавайте с картофельным пюре')
insert into Steps(StepName) values ('Подавать в горячем виде.')
insert into Steps(StepName) values ('Подготовьте все необходимые ингредиенты.')
insert into Steps(StepName) values ('Подготовьте все необходимые ингредиенты. Яйца отварите вкрутую.')
insert into Steps(StepName) values ('Подготовьте заправку: смешайте в небольшой креманке свежевыжатый лимонный сок, горчицу и немного оливкового масла. Полейте готовой смесью салат, присыпьте кунжутными семечками. Они хоть и не входят в классический вариант салата «Цезарь», очень хорошо сочетаются с овощами и придают салату пикантность. Подавайте салат в качестве закуски перед основным блюдом или в качестве главного блюда.')
insert into Steps(StepName) values ('Подготовьте ингредиенты.')
insert into Steps(StepName) values ('Позволить маслу нагреться, тогда обжаривать мясо со всех сторон в течение 20 минут.')
insert into Steps(StepName) values ('Помидор очистите и порежьте нетолстыми дольками.')
insert into Steps(StepName) values ('После 4-ой варки варенье из айвы готово и выглядит оно замечательно, и на вкус приятно.')
insert into Steps(StepName) values ('После 5 минут обжарки, добавьте воды и уменьшите огонь до средне-низкого уровня.')
insert into Steps(StepName) values ('После этого добавить в сковороду порезанную тонкими брусочками колбасу и соломкой - болгарский перец.')
insert into Steps(StepName) values ('Поставить пирог в духовой шкаф и выпекать до золотистой корочки при температуре 180 гр.')
insert into Steps(StepName) values ('Приготавливаем соус: смешиваем воду, со сметаной, томатной пастой и мукой. Все хорошо перемешиваем при помощи венчика.')
insert into Steps(StepName) values ('Приготовить айву.')
insert into Steps(StepName) values ('Проварить 2 минуты, выключить.')
insert into Steps(StepName) values ('Разбейте 3 яйца в миску с теплым молоком.')
insert into Steps(StepName) values ('Сам лаваш получается хрустящим, а внутри сочная и очень вкусная начинка.')
insert into Steps(StepName) values ('Свернуть лаваш в рулет.')
insert into Steps(StepName) values ('Сложить айву в кастрюлю для варки варенья.')
insert into Steps(StepName) values ('Сложить в кастрюлю, влить 500 мл воды, довести до кипения и варить 20-30 минут до мягкости айвы.')
insert into Steps(StepName) values ('Смазать лаваш смесью майонеза, кетчупа и горчицы (количество регулируем под свой вкус).')
insert into Steps(StepName) values ('Смешайте венчиком или миксером.')
insert into Steps(StepName) values ('Смешать муку и дрожжи, постепенно соединить сухие и жидкие ингредиенты. Замесить мягкое тесто, накрыть полотенцем и убрать для подхода на 1 час в теплое место.')
insert into Steps(StepName) values ('Смешиваем.')
insert into Steps(StepName) values ('Соберите салат. На дно тарелки положите промытые листья салата, сверху положите помидоры, сухарики, порезанные дольками яйца и кусочки курицы.')
insert into Steps(StepName) values ('Сосиски в лаваше выложить на разогретую с растительным маслом сковороду.')
insert into Steps(StepName) values ('Сосиски в лаваше, приготовленные по этому простому рецепту, станут отличным перекусом.')
insert into Steps(StepName) values ('Спустя 3 дня готовое мясо достаньте из маринада, не ополаскивайте.')
insert into Steps(StepName) values ('Сразу же добавьте нарезанный лук.')
insert into Steps(StepName) values ('Сюда же добавить и помидор, порезанный небольшими кубиками.')
insert into Steps(StepName) values ('Такой мармелад можно хранить в закрытой сухой стеклянной посуде всю зиму.')
insert into Steps(StepName) values ('Творожный чизкейк готов. Подавать охлаждённым, блюдо можно украсить шариками мороженного, покрытыми тёртым шоколадом.')
insert into Steps(StepName) values ('Тесто выложить на дно формы, сделать бортики. Выложить начинку и оставить подходить на 20 минут.')
insert into Steps(StepName) values ('Тесто выпекаем в духовом шкафу 8-10 минут при температуре 180 градусов.')
insert into Steps(StepName) values ('Тушите мясо до мягкости. Соус должен стать однородным и достаточно густым, чтобы не стекал с ложки.')
insert into Steps(StepName) values ('Тушить все на небольшом огне около 10 минут.')
insert into Steps(StepName) values ('Тщательно всё перемешайте внутри пакета.')
insert into Steps(StepName) values ('Тщательно протереть тряпочкой всю ее поверхность, чтобы убрать невидимый пушок из ворсинок.')
insert into Steps(StepName) values ('Уберите мясо в холодильник на 3 дня.')
insert into Steps(StepName) values ('Убираем лук из сковороды.')
insert into Steps(StepName) values ('Убираем масло с огня и добавляем уксус.')
insert into Steps(StepName) values ('Уложите мясо в пакет.')
insert into Steps(StepName) values ('Хлеб порежьте на небольшие кубики и подсушите в духовке или микроволновке.')
insert into Steps(StepName) values ('Хорошо помешайте.')
insert into Steps(StepName) values ('Через сутки повторить варку.')
insert into Steps(StepName) values ('Я готовила три порции и у меня получились вот такие три рулета.')
insert into Steps(StepName) values ('Яйцо взбить с сахаром, ванильным сахаром и солью, добавить сметану, мягкое масло, перемешать.')